<?php

namespace Database\Seeders;

use App\Models\Article;
use App\Models\Identity;
use Illuminate\Database\Seeder;
use Illuminate\Foundation\Auth\User;
use Illuminate\Support\Facades\DB;

class ArticleSeeder extends Seeder
{
    public function run(): void
    {

        Article::truncate();
        $faker = \Faker\Factory::create();

        $md = "";
        $listImage = array(
            "http://127.0.0.1:3000/assets/images/23774-12-4280744934.jpeg",
            "http://127.0.0.1:3000/assets/images/0231de96df2e7e9a84da5f127381f750.jpg",
            "http://127.0.0.1:3000/assets/images/Abstract+Wallpapers+For+Desktop+2-355631041.jpeg",
        );

        $image = "https://www.coocobolo.com/_next/image/?url=%2Fimages%2F22707-11-171511848.jpeg&w=1920&q=100";
        $users = DB::connection("identity_db_server")
            ->table("identities")
            ->select('id')
            ->get();

        for ($i = 0; $i < 10; $i++) {
            $categories_id = range(1, 10);
            $random_time = range(1, 10);
            $random_uid = rand(0, 1);
            $randomImageInt = rand(0, 2);
            shuffle($categories_id);
            $selectedCategories = array_slice($categories_id, 0, 2);


            $article = new Article(
                [
                    'title' => $faker->words(10, true),
                    'slug' => $faker->slug,
                    'description' => $faker->words(14, true),
                    'content' => $md,
                    'image_cover' => $listImage[$randomImageInt],
                    'status' => 'published',
                    'visit_count' => 0,
                    'author_id' => $users[$random_uid]->id,
                    'reading_time' => (string)$random_time[$i]
                ]
            );

            $article->save();
            $article->categories()->attach($selectedCategories);
        }
    }
}
